import React, { Component } from 'react';
import {
    Progress,
    Button,
    Container, Row, Col,
} from 'reactstrap';
import Timer from 'react-timer-wrapper';
import Timecode from 'react-timecode';
import Answer from '../Answer/Answer';
import './Test.css';

class Test extends Component {
    constructor(props) {
        super(props);
        this.state = {
          showAnswer: false,
          timerActive: false
        };
        this.hideComponent = this.hideComponent.bind(this);
    }

    hideComponent() {
        this.setState({
            showAnswer: !this.state.showAnswer,
            timerActive: true
        });
    }

    render() {
        const { showAnswer } = this.state;

        return (
            <div className="test">
                <Container>
                    <div className="dark-overlay"></div>
                    <Row>
                        <Col sm={{ size: 10, offset: 1 }}>
                            <Row>
                                <Col className="test-timer">
                                    <Timer active={this.state.timerActive} duration={null} className="h4">
                                        <i className="fas fa-stopwatch" />
                                        <span>  </span>
                                        <Timecode />
                                    </Timer>
                                </Col>
                            </Row>
                            <Row>
                                <Col sm={{ size: 10, offset: 1 }}>
                                    <Progress animated className="progress" color="success" value="25">1/5</Progress>
                                </Col>
                            </Row>
                            { showAnswer ? 
                                <React.Fragment>
                                    <Row>
                                        <Col className="test-category">
                                            <i className="fab fa-css3-alt" /> CSS
                                        </Col>
                                    </Row>
                                    <div className="question">
                                        <h4>What are CSS selectors?</h4>
                                    </div>
                                    <Answer />
                                </React.Fragment> : 
                                <Button color="primary" id="start_btn" onClick={() => this.hideComponent()}>START</Button>
                            }
                        </Col>
                    </Row>
                </Container>
            </div>
        );
    }
}

export default Test;
