import React, { Component } from 'react';
import {
    Button,
    Container, Row, Col,
} from 'reactstrap';
import Answer from '../Answer/Answer';
import Timer from '../Timer/Timer'
import './Test.css';

const timer = document.querySelector('#time');
const start_btn = document.querySelector('#start_btn');
const pause_btn = document.querySelector('#pause_btn');
const answer = document.querySelector('#answer-textarea');
let time = 0,
    interval;

class Test extends Component {
    constructor(props) {
        super(props);
        this.state = {
          showAnswer: false
        };
        this.hideComponent = this.hideComponent.bind(this);
    }

    hideComponent() {
        this.setState({
            showAnswer: !this.state.showAnswer
        });
        console.log("Button clicked: " + this.state.showAnswer);
    }

    showTime() {
        time += 1;
    }

    toHHMMSS(time) {
        let hours = Math.floor(time / 3600);
        let minutes = Math.floor((time - hours * 3600) / 60);
        let seconds = time - hours * 3600 - minutes * 60;

        hours = `<i class="fas fa-stopwatch"></i> ${hours}`.padStart(2, '0');
        minutes = `${minutes}`.padStart(2, '0');
        seconds = `${seconds}`.padStart(2, '0');

        return hours + ':' + minutes + ':' + seconds;
    }

    startTimer() {
        interval = setInterval(this.showTime, 1000);
    }

    render() {
        const { showAnswer } = this.state;

        return (
            <div className="Test">
                <div className="dark-overlay"></div>
                <Container className="d-flex justify-content-center align-items-center">
                    <Row>
                        <Col sm={{ size: 10, offset: 1 }}>
                            <Row className="category">
                                <h1 className="mb-0 text-green text-center"><i className="fab fa-css3-alt" /> CSS </h1>
                            </Row>
                            <Row>
                                <Col className="progress text-center mb-3">
                                    <div className="progress-value">Question 3 of 5</div>
                                </Col>
                            </Row>
                            <div className="question">
                                <h4 className="row text-white">What are CSS selectors?</h4>
                            </div>
                            { showAnswer ? 
                            <Answer /> : 
                            <Button variant="primary" className="btn btn-primary" id="start_btn" onClick={() => {
                                this.hideComponent();
                                this.startTimer();
                            }}>START</Button>
                             }
                             {this.toHHMMSS(time)}
                        </Col>
                    </Row>
                </Container>
            </div>
        );
    }
}

export default Test;
